        public void modifyGridView1()
        {

            DataGridViewColumnCollection columns = this.dataGridView1.Columns;
            
            if (columns.Count > 0)
            {
                foreach(DataGridViewColumn column in columns)
                {
                    String headerText = column.HeaderText;
                    
                    if(headerText == DataGridView1Headers.Subject.ToString())
                    {
                        column.FillWeight = 340;
                    }
                    else if(headerText == DataGridView1Headers.InstructorName.ToString())
                    {
                        column.FillWeight = 136;
                        column.HeaderText = "Instructor";
                    }
                    else if(headerText == DataGridView1Headers.RoomNumber.ToString())
                    {
                        column.FillWeight = 30;
                        column.HeaderText = "Room#";
                        column.DefaultCellStyle.Font = new Font("Segoe UI", 15,FontStyle.Bold); 
                    }
                    else if( headerText == DataGridView1Headers.Date.ToString())
                    {
                        //Setting visibility to false here as we are displaying daily schedules therefore having a date section is redundant.
                        column.Visible = false;
                    }
                    else if( headerText == DataGridView1Headers.StartTime.ToString() || headerText == DataGridView1Headers.EndTime.ToString())
                    {
                        column.FillWeight = 59;
                    }
                }
            } 
        }

        private void dataGridView1_CellPainting(object sender, DataGridViewCellPaintingEventArgs e)
        {

           if(e.Value == null && e.ColumnIndex == 0)
            {
                e.AdvancedBorderStyle.Bottom = DataGridViewAdvancedCellBorderStyle.None;
            }
           else if(e.Value != null &&  e.ColumnIndex == 0)
            {
                e.AdvancedBorderStyle.Top = DataGridViewAdvancedCellBorderStyle.Single;
                e.AdvancedBorderStyle.Bottom = DataGridViewAdvancedCellBorderStyle.None;

            }

           if(e.ColumnIndex == 0)
            {
                e.AdvancedBorderStyle.Left = DataGridViewAdvancedCellBorderStyle.Single;
            }
        }


